import { useEffect, useState } from "react"
import { useParams } from "react-router-dom"
import pgAxios from "../api/pgAxios"

export const BusMatrix = () =>{
    const ROUTE_API = '/api/find-station/'
    const BUS_API = '/api/find-seats-by-bus/'

    const {id} = useParams()
    const [seats,setSeats] = useState([])




    var columns = 4
    var k = 0
    var lista = []

    for(let i = 0; i<seats.length; i++){
        if(k == columns){
            lista.push(<br/>)
            k=0
        }
        k++
        lista.push(<input type={"checkbox"}/>)
    }


    const searchRoute = async () => {
        try {

            await pgAxios.get(BUS_API+ `${id}`)
            .then((res)=> {
                setSeats(res.data)
                console.log(res.data)
            })

        } catch (error) {
            throw error 
        }
    }


    useEffect(()=>{searchRoute()},[])



    return (
        <>
        <h1>Matrix</h1>
        <h3>Select a seat</h3>
        {/* {
        seats.map((el)=>(
            <>
            <input key={el.id} type={"checkbox"}/> 
            </>
        ))} */}
        {lista}

        </>
    )

}
